form.script:



local function addSkill(nome, mod, treino, armadura)
	local skill = self.rclPericias:append();
	if skill == nil then return end;

	skill.nome = nome;
	skill.mod = mod;
	skill.treino = treino;
	skill.armadura = armadura;
end;

local function tormenta20()
